import React, { useState } from "react";
import {
  Container,
  Titulo,
  LogoSpan,
  BeneficioDiv,
  BeneficioIcone,
  BeneficioDescricao,
  BotaoDiv,
  BotaoContratacao,
  NavegarCardDiv,
  BotaoSair
} from "./Style";
import Logo from "../../imagens/logo-gargo-premium.png";
import Cartao from "../../imagens/cartao-credito-checkout.png";
import Engrenagem from "../../imagens/engrenagem-checkout.png";
import Relogio from "../../imagens/relogio-checkout.png";

const Checkout = ({ fecharCheckout }) => {
  const [etapa, setEtapa] = useState(1);

  const avancarEtapa = () => {
    if (etapa < 4) {
      setEtapa(etapa + 1);
    }
  };

  const renderizarBeneficio = () => {
    switch (etapa) {
      case 1:
        return {
          icone: Cartao,
          descricao:
            "Com Gargo Premium você oferece Pix, crédito e boleto com agilidade!"
        };
      case 2:
        return {
          icone: Engrenagem,
          descricao:
            "Implementação facilitada para integração rápida e eficiente!"
        };
      case 3:
        return {
          icone: Relogio,
          descricao:
            "Acompanhe suas faturas em tempo real e tenha controle total!"
        };
      case 4:
        return {
          icone: null,
          descricao: null
        };
      default:
        return null;
    }
  };

  const beneficio = renderizarBeneficio();

  const handleBotaoSairClick = () => {
    fecharCheckout(); // Chame a função fecharCheckout quando o botão "Sair" for clicado
  };

  return (
    <Container>
      <LogoSpan>
        <img src={Logo} alt="Logo" />
      </LogoSpan>
      <Titulo>
        {etapa === 1 && "PAGAMENTO RÁPIDO E FÁCIL"}
        {etapa === 2 && "IMPLEMENTAÇÃO FACILITADA"}
        {etapa === 3 && "FATURAS EM TEMPO REAL"}
        {etapa === 4 && "CONTRATE AGORA"}
      </Titulo>
      {etapa < 4 && (
        <BeneficioDiv>
          <BeneficioIcone>
            <img src={beneficio.icone} alt="Ícone do Benefício" />
          </BeneficioIcone>
          <BeneficioDescricao>{beneficio.descricao}</BeneficioDescricao>
        </BeneficioDiv>
      )}
      {etapa === 4 && (
        <BeneficioDiv>
          <BeneficioDescricao>
            <label htmlFor="numeroCartao">Número do Cartão:</label>
            <input type="text" id="numeroCartao" name="numeroCartao" required />

            <label htmlFor="nomeCartao">Nome impresso no cartão:</label>
            <input type="text" id="nomeCartao" name="nomeCartao" required />

            <label htmlFor="validadeCartao">Validade (MM/AA):</label>
            <input type="text" id="validadeCartao" name="validadeCartao" required />

            <label htmlFor="cnpj">CNPJ:</label>
            <input type="text" id="cnpj" name="cnpj" required />
          </BeneficioDescricao>
        </BeneficioDiv>
      )}
      <BotaoDiv>
        {etapa < 4 && (
          <BotaoContratacao onClick={avancarEtapa}>
            {etapa === 3 ? "CONTRATE AGORA!" : "Avançar"}
          </BotaoContratacao>
        )}
        {etapa === 4 && (
          <BotaoContratacao onClick={handleFinalizarPagamento}>
            Finalizar Pagamento
          </BotaoContratacao>
        )}
      </BotaoDiv>
      <NavegarCardDiv>
        <BotaoSair onClick={handleBotaoSairClick}>Sair</BotaoSair>
      </NavegarCardDiv>
    </Container>
  );
};

export default Checkout;
