import { styled} from "styled-components";
import Dispositivo from "../utils";

const ContainerHome = styled.div`
    display: flex;
    flex-direction: column;
    position: relative;
    padding-top: 20px;
    left: 40px;
    top: 100px;
    max-width: 1100px;
    gap:80px;

    flex-wrap: wrap;
    justify-content: flex-start;
    transition: flex-grow 0.6s ease-in-out;

    &.menuAberto {
      margin-left: 20%; /* Ajuste o valor conforme necess치rio */
    };

    @media screen and (min-width: ${Dispositivo.desktop}px) {
        height: 720px;

    &.menuAberto {
      margin-left: 0%; /* Ajuste o valor conforme necess치rio */
      width:80%;
    };

    @media screen and (min-width: ${Dispositivo.desktop}px) {
      height: 720px;
    };

    @media screen and (min-width: ${Dispositivo.smallMobile}px) {
        margin-top: 40px;
        left: 0%;

      };

`;

export default Container;
