import styled from "styled-components";
import Dispositivo from "../../utils";



const Container = styled.div`
    display: flex;
    flex-direction: column;
    position: relative;
    padding-top: 20px;
    left: 40px;
    top: 100px;
    gap:80px;

    flex-wrap: wrap;
    justify-content: flex-start;
    align-content: space-evenly;
    transition: flex-grow 0.3s ease-in-out;


    @media screen and (min-width: ${Dispositivo.desktop}px) {
        height: 720px;
        position: absolute;
        max-width: 1100px;
        left: 17px;
        top: 15px;
        background: #9747FF;
        border-radius: 17px;
    };

    @media screen and (min-width: ${Dispositivo.smallMobile}px) {
        position: absolute;
        width: 288px;
        height: 476px;
        left: 17px;
        top: 15px;
        background: #9747FF;
        border-radius: 17px;
    };
`;

    const Titulo = styled.span`



    @media screen and (min-width: ${Dispositivo.smallMobile}px) {
        /* PAGAMENTO RÁPIDO E FÁCIL */

        position: relative;
        width: 238px;
        height: 46px;

        font-family: 'Roboto';
        font-style: normal;
        font-weight: 900;
        font-size: 20px;
        line-height: 23px;
        text-align: center;
        color: #F1F1F1;
    };
`;



export {Container, Titulo};
