import React, { useState } from "react";
import {
  Container,
  Titulo,
  LogoSpan,
  BeneficioDiv,
  BeneficioIcone,
  BeneficioDescricao,
  BotaoDiv,
  BotaoContratacao,
  NavegarCardDiv,
  BotaoSair,
  BotaoAvancar
} from "./Style";
import Logo from "../../imagens/logo-gargo-premium.png";
import Engrenagem from "../../imagens/engrenagem-checkout.png";
import Relogio from "../../imagens/relogio-checkout.png";
import Cartao from "../../imagens/cartao-credito-checkout.png";

const Checkout = () => {
  const [currentCard, setCurrentCard] = useState("checkout");

  const handleAvancar = () => {
    if (currentCard === "checkout") {
      setCurrentCard("card1");
    } else if (currentCard === "card1") {
      setCurrentCard("card2");
    }
  };

  const handleSair = () => {
    if (currentCard === "card1") {
      setCurrentCard("checkout");
    } else if (currentCard === "card2") {
      setCurrentCard("card1");
    }
  };

  const renderBeneficioDivContent = () => {
    if (currentCard === "checkout") {
      return (
        <>
          <BeneficioIcone>
            <img src={Cartao} alt="Cartão" />
          </BeneficioIcone>
          <BeneficioDescricao>
            Com Gargo Premium você oferece Pix, crédito e boleto com agilidade!
          </BeneficioDescricao>
        </>
      );
    } else if (currentCard === "card1") {
      return (
        <>
          <BeneficioIcone>
            <img src={Engrenagem} alt="Engrenagem" />
          </BeneficioIcone>
          <BeneficioDescricao>
            Personalize suas configurações para obter o máximo de benefícios!
          </BeneficioDescricao>
        </>
      );
    } else if (currentCard === "card2") {
      return (
        <>
          <BeneficioIcone>
            <img src={Relogio} alt="Relógio" />
          </BeneficioIcone>
          <BeneficioDescricao>
            Aproveite de forma ilimitada todos os benefícios do Gargo Premium!
          </BeneficioDescricao>
        </>
      );
    }
  };

  return (
    <Container>
      <LogoSpan>
        <img src={Logo} alt="Logo" />
      </LogoSpan>
      <Titulo>PAGAMENTO RÁPIDO E FÁCIL</Titulo>
      <BeneficioDiv>{renderBeneficioDivContent()}</BeneficioDiv>
      <BotaoDiv>
        <BotaoContratacao>CONTRATE AGORA!</BotaoContratacao>
      </BotaoDiv>
      <NavegarCardDiv>
        <BotaoSair onClick={handleSair}>Sair</BotaoSair>
        <BotaoAvancar onClick={handleAvancar}>&gt;&gt;</BotaoAvancar>
      </NavegarCardDiv>
    </Container>
  );
};

export default Checkout;
