import styled from "styled-components";
import Dispositivo from "../../utils";



const Container = styled.div`
    display: flex;
    flex-direction: column;
    position: relative;
    padding-top: 20px;
    left: 40px;
    top: 100px;
    gap:80px;

    flex-wrap: wrap;
    justify-content: flex-start;
    align-content: space-evenly;
    transition: flex-grow 0.3s ease-in-out;


    @media screen and (min-width: ${Dispositivo.desktop}px) {
        height: 720px;
        position: absolute;
        max-width: 1100px;
        left: 17px;
        top: 15px;
        background: #9747FF;
        border-radius: 17px;
    };

    @media screen and (min-width: ${Dispositivo.smallMobile}px) {
        position: absolute;
        width: 288px;
        height: 476px;
        left: 17px;
        top: 15px;
        background: #9747FF;
        border-radius: 17px;
    };
`;

const LogoSpan = styled.span`
    width: 187px;
    height: 61px;
`;


const Titulo = styled.span`



    @media screen and (min-width: ${Dispositivo.smallMobile}px) {
        /* PAGAMENTO RÁPIDO E FÁCIL */

        position: relative;
        width: 238px;
        height: 46px;

        font-family: 'Roboto';
        font-style: normal;
        font-weight: 900;
        font-size: 20px;
        line-height: 23px;
        text-align: center;
        color: #F1F1F1;
    };
`;

const BeneficioDiv = styled.div`
    width: 100%;
    height: 88px;
`;

const BeneficioIcone = styled.span`
    /* Vector */

    left: 11.25%;
    right: 57.08%;
    top: 44.62%;
    bottom: 40.51%;

`;

const BeneficioDescricao = styled.span`
    /* Com Gargo Premium você oferece Pix, crédito e boleto com agilidade! */


    width: 144px;
    height: 87.88px;

    font-family: 'Roboto';
    font-style: normal;
    font-weight: 500;
    font-size: 14.8235px;
    line-height: 17px;

    color: #F1F1F1;
`;

export {Container, LogoSpan, Titulo, BeneficioDiv, BeneficioIcone, BeneficioDescricao};
